INSERT INTO `tb_blog` VALUES ('18', '19', '18', 'with…as结构的应用2', '<p> 用地递归的方式处理数据的层级关系，是对with…as 的一个重要应用方式 </p> \n<p> drop table smMenu </p> create table &nbsp;smMenu (ID varchar(50),menuname varchar(50),menuclassname varchar(50),parentID varchar(50))\n<br> insert into smMenu values(\'0\',\'根目录\',\'class0\',null)\n<br> insert into smMenu values(\'01\',\'销售管理\',\'class01\',\'0\')\n<br> insert into smMenu values(\'0101\',\'报价管理\',\'class0101\',\'01\')\n<br> insert into smMenu values(\'010101\',\'制订报价单\',\'class010101\',\'0101\')\n<br> insert into smMenu values(\'010102\',\'报价单查询\',\'class010102\',\'0101\') &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;\n<br> insert into smMenu values(\'0102\',\'订单管理\',\'class0102\',\'01\')\n<br> insert into smMenu values(\'010201\',\'订单录入\',\'class010201\',\'0102\')\n<br> insert into smMenu values(\'010202\',\'订单审核\',\'class010202\',\'0102\')\n<br> insert into smMenu values(\'02\',\'计划管理\',\'class02\',\'0\')\n<br> insert into smMenu values(\'0201\',\'主计划\',\'class0201\',\'02\')\n<br> insert into smMenu values(\'020101\',\'MRP计划\',\'class020101\',\'0201\');\n<br> \n<br> \n<br> &nbsp;with cte as &nbsp;\n<br> &nbsp;( &nbsp;\n<br> &nbsp;select a.ID,a.menuname,a.parentID from smMenu a where id=\'0\' &nbsp;\n<br> &nbsp;union all &nbsp;&nbsp;\n<br> &nbsp;select k.ID,k.menuname,k.parentID &nbsp;from smMenu k inner join cte c on c.id = k.parentID &nbsp;\n<br> &nbsp;)\n<br> \n<p> &nbsp;select * from cte &nbsp;order by id </p> \n<p> 注：Oracle,sqlserver,均支持；MySQL不支持这种临时视图,其他with…as结构应用参考https://weibo.com/5193584071/E5534cEdV </p>', '0', '1', '1', '2019-05-23 23:56:11', '2019-05-23 23:56:11');
INSERT INTO `tb_blog` VALUES ('19', '3', '9', 'java-十进制、八进制、十六进制数互相转换', '<p> 懒人不愿做题，所有的东西都用程序跑出来是最好的，下面做一个十进制、八进制、十六进制数互相转换的小程序 </p> \n<p> <br> </p> \n<pre class=\" java;\">public class JZTurn {\r\n\r\n	public static void main(String[] args) {\r\n		int num2 = 1314521;\r\n		System.out.println(\"十进制数:\"+num2);\r\n		System.out.println(\"十进制转换二进制:\"+turn2(num2));\r\n		System.out.println(\"十进制转换八进制:\"+turn8(num2));\r\n		System.out.println(\"十进制转换十六进制:\"+turn16(num2));\r\n		\r\n		//2-&gt; 8\r\n		System.out.println(\"二进制转换八进制:\"+twoTurnEight(turn2(num2)));\r\n		System.out.println(\"八进制转换二进制:\"+eightTurnTwo(turn8(num2)));\r\n		System.out.println(\"八进制转换十六进制:\"+eightTurnSixteen(turn8(num2)));\r\n		//2-&gt;16\r\n		System.out.println(\"二进制转换十六进制:\"+twoTurnSixteen(turn2(num2)));\r\n	}\r\n	\r\n	/**\r\n	 * 10进制转二进制\r\n	 * @param num\r\n	 * @return\r\n	 */\r\n	public static String turn2(int num){\r\n		String shortTimeNum = \"\";\r\n		int remainder = 0;\r\n		/**\r\n		 * 模拟短除法\r\n		 */\r\n		while(num &gt;= 1){\r\n			remainder = num % 2;\r\n			num = num / 2;\r\n			shortTimeNum = remainder + shortTimeNum;\r\n		}\r\n		\r\n		return shortTimeNum;\r\n	}\r\n	\r\n	/**\r\n	 * 10进制转8进制\r\n	 * @param num\r\n	 * @return\r\n	 */\r\n	public static String turn8(int num){\r\n		String shortTimeNum = \"\";\r\n		int remainder = 0;\r\n		/**\r\n		 * 模拟短除法\r\n		 */\r\n		while(num &gt;= 1){\r\n			remainder = num % 8;\r\n			num = num / 8;\r\n			shortTimeNum = remainder + shortTimeNum;\r\n		}\r\n		\r\n		return shortTimeNum;\r\n	}\r\n	\r\n	/**\r\n	 * 10进制转16进制\r\n	 * @param num\r\n	 * @return\r\n	 */\r\n	public static String turn16(int num){\r\n		String shortTimeNum = \"\";\r\n		int remainder = 0;\r\n		String tempStr = \"\";\r\n		/**\r\n		 * 模拟短除法\r\n		 */\r\n		while(num &gt;= 1){\r\n			remainder = num % 16;\r\n			tempStr = getOtherNum(remainder);\r\n			\r\n			num = num / 16;\r\n			shortTimeNum = tempStr + shortTimeNum;\r\n		}\r\n		\r\n		return shortTimeNum;\r\n	}\r\n	\r\n	/**\r\n	 * 2进制转换8进制\r\n	 * 概念说明:这里转换的是整数,从右向左三位一组分别乘以2的零次方，2的一次方，2的2次方\r\n	 * 然后把每组中的数相加，再把各组从左向右拼接到一起\r\n	 * @param strNum2\r\n	 */\r\n	public static String twoTurnEight(String strNum2){\r\n		String resultNum = \"\";\r\n		int remainder = strNum2.length() % 3;\r\n		\r\n		//补位 三位一组,最后一组位数不够用0补充\r\n		switch(remainder){\r\n		case 1:\r\n			strNum2 = \"00\"+strNum2;\r\n			break;\r\n		case 2:\r\n			strNum2 = \"0\"+strNum2;\r\n			break;\r\n		}\r\n		int index = strNum2.length()-1;\r\n		int tempNum1 = 0;\r\n		int tempNum2 = 0;\r\n		int tempNum3 = 0;\r\n		while(index&gt;=1){\r\n\r\n			index -- ;\r\n			\r\n			if(index % 3 == 0){\r\n				//每个数为一组\r\n				tempNum1 = Integer.parseInt(strNum2.substring(index, index+1));\r\n				tempNum2 = Integer.parseInt(strNum2.substring(index+1, index+2));\r\n				tempNum3 = Integer.parseInt(strNum2.substring(index+2, index+3));\r\n				\r\n				resultNum = tempNum3*1+tempNum2*2+tempNum1*4+resultNum;\r\n				\r\n			}\r\n			\r\n		}\r\n		\r\n		return resultNum;\r\n	}\r\n	\r\n	/**\r\n	 * 八进制转换二进制\r\n	 * @param eightNum\r\n	 * @return\r\n	 */\r\n	public static String eightTurnTwo(String eightNum){\r\n		String twoNum = \"\";\r\n		int index = eightNum.length()-1;\r\n		\r\n		String tempNum = \"\";\r\n		while(index&gt;=0){\r\n			\r\n			tempNum = turn2(Integer.parseInt(eightNum.substring(index, index+1))) ;\r\n			\r\n			//补位,在转换8进制时是每三为二进制数为一组,转换回二进制时位数也需要是三位,不够用零补\r\n			switch (tempNum.length()) {\r\n			\r\n			case 1:\r\n				tempNum = \"00\"+tempNum;\r\n				break;\r\n\r\n			case 2:\r\n				tempNum = \"0\"+tempNum;\r\n				break;\r\n			}\r\n			\r\n			twoNum = tempNum + twoNum;\r\n			index--;\r\n		}\r\n		\r\n		return twoNum;\r\n	}\r\n	\r\n	/**\r\n	 * 2进制转换16进制\r\n	 * 概念说明:这里转换的是整数,从右向左四位一组分别乘以2的零次方，2的一次方，2的2次方，2的3次方,\r\n	 * 然后相加把每组最终的得数一次从左向右拼到一起,若其中一组的和大于9,按照对应关系转换后再把每组的结果拼接到一起\r\n	 * @param strNum2\r\n	 */\r\n	public static String twoTurnSixteen(String strNum2){\r\n		String resultNum = \"\";\r\n		int remainder = strNum2.length() % 4;\r\n		\r\n		//补位,四位一组,最后一组位数不够用0补充\r\n		switch(remainder){\r\n		case 1:\r\n			strNum2 = \"000\"+strNum2;\r\n			break;\r\n		case 2:\r\n			strNum2 = \"00\"+strNum2;\r\n			break;\r\n		case 3:\r\n			strNum2 = \"0\"+strNum2;\r\n			break;\r\n		}\r\n		int index = strNum2.length()-1;\r\n		int tempNum1 = 0;\r\n		int tempNum2 = 0;\r\n		int tempNum3 = 0;\r\n		int tempNum4 = 0;\r\n		int tempNum5 = 0;\r\n		while(index&gt;=1){\r\n\r\n			index -- ;\r\n			\r\n			if(index % 4 == 0){\r\n				//每个数为一组\r\n				tempNum1 = Integer.parseInt(strNum2.substring(index, index+1));\r\n				tempNum2 = Integer.parseInt(strNum2.substring(index+1, index+2));\r\n				tempNum3 = Integer.parseInt(strNum2.substring(index+2, index+3));\r\n				tempNum4 = Integer.parseInt(strNum2.substring(index+3, index+4));\r\n				\r\n				tempNum5 = tempNum4*1 + tempNum3*2+tempNum2*4+tempNum1*8;\r\n				\r\n				resultNum = getOtherNum(tempNum5) +resultNum;\r\n				\r\n			}\r\n			\r\n		}\r\n		\r\n		return resultNum;\r\n	}\r\n	\r\n	/**\r\n	 * 8进制转换16进制\r\n	 * @param strNum2\r\n	 */\r\n	public static String eightTurnSixteen(String strNum2){\r\n		return twoTurnSixteen(eightTurnTwo(strNum2));\r\n	}\r\n	\r\n	/**\r\n	 * 16进制转换中的特殊处理,需要把大于9的数字转换成字母\r\n	 * @param tempNum\r\n	 * @return\r\n	 */\r\n	public static String getOtherNum(int tempNum){\r\n		String tempStr = \"\";\r\n		if(tempNum &gt; 9){\r\n			switch(tempNum){\r\n			case 10:\r\n				tempStr = \"A\";\r\n				break ;\r\n			case 11:\r\n				tempStr = \"B\";\r\n				break;\r\n			case 12:\r\n				tempStr = \"C\";\r\n				break;\r\n			case 13:\r\n				tempStr = \"D\";\r\n				break;\r\n			case 14:\r\n				tempStr = \"E\";\r\n				break;\r\n			case 15:\r\n				tempStr = \"F\";\r\n				break;\r\n			}\r\n		}else{\r\n			tempStr = String.valueOf(tempNum);\r\n		}\r\n		return tempStr;\r\n	}\r\n}</pre> 输出结果: \n<p> 十进制数:1314521<br> 十进制转换二进制:101000000111011011001<br> 十进制转换八进制:5007331<br> 十进制转换十六进制:140ED9<br> 二进制转换八进制:5007331<br> 八进制转换二进制:101111011011001<br> 八进制转换十六进制:5ED9<br> 二进制转换十六进制:140ED9 </p> \n<p> **********************************没有优化程序性能，有时间再改********************************* </p>', '0', '1', '6', '2019-05-23 23:58:53', '2019-05-23 23:58:53');
