INSERT INTO `tb_blog` VALUES ('22', '6', '28', 'ECMAScript6（es6）实现并集(union)、交集(intersection)、差集(difference)', '<h2> 并集(union) </h2> \n<pre class=\" js;\">let a = new Set([1,2,3]);\r\nlet b = new Set([4,3,2]);\r\nlet union = new Set([...a, ...b]);          // {1,2,3,4}</pre> \n<br> \n<h2> 交集(intersection) </h2> \n<pre class=\" js;\">let a = new Set([1,2,3]);\r\nlet b = new Set([4,3,2]);\r\nlet intersection = new Set([...a].filter(x =&gt; b.has(x)));       // {2,3}</pre> \n<br> \n<h2> 差集(difference) </h2> \n<pre class=\" js;\">let a = new Set([1,2,3]);\r\nlet b = new Set([4,3,2]);\r\nlet difference = new Set([...a].filter(x =&gt; !b.has(x)));        // {1}</pre> \n<br> \n<p> 参考：http://2ality.com/2015/01/es6-set-operations.html </p> \n<p> <br> </p> \n<p> <br> </p>', '0', '1', '0', '2019-05-24 00:02:02', '2019-05-24 00:02:02');
