INSERT INTO `tb_blog` VALUES ('31', '6', '3', 'Jquery动态构建form', '<pre class=\" js;\">jQuery(function ($) {\r\n	$.extend({\r\n		form: function (url, data, method) {\r\n			if (method == null) method = \'POST\';\r\n			if (data == null) data = {};\r\n\r\n			var form = $(\'&lt;form&gt;\').attr({\r\n				method: method,\r\n				action: url\r\n			}).css({\r\n				display: \'none\'\r\n			});\r\n\r\n			var addData = function (name, data) {\r\n				if ($.isArray(data)) {\r\n					for (var i = 0; i &lt; data.length; i++) {\r\n						var value = data[i];\r\n						addData(name + \'[]\', value);\r\n					}\r\n				} else if (typeof data === \'object\') {\r\n					for (var key in data) {\r\n						if (data.hasOwnProperty(key)) {\r\n							addData(name + \'[\' + key + \']\', data[key]);\r\n						}\r\n					}\r\n				} else if (data != null) {\r\n					form.append($(\'&lt;input&gt;\').attr({\r\n						type: \'hidden\',\r\n						name: String(name),\r\n						value: String(data)\r\n					}));\r\n				}\r\n			};\r\n\r\n			for (var key in data) {\r\n				if (data.hasOwnProperty(key)) {\r\n					addData(key, data[key]);\r\n				}\r\n			}\r\n\r\n			return form.appendTo(\'body\');\r\n		}\r\n	});\r\n});</pre> \n<p> 调用方式： </p> \n<p> </p>\n<pre><span style=\"font-style:italic;\">$</span>.<span style=\"color:#7a7a43;\">form</span>(<span style=\"color:#008000;font-weight:bold;\">\'xxxurl\'</span>, <span style=\"color:#458383;\">$json</span>, <span style=\"color:#008000;font-weight:bold;\">\'POST\'</span>).<span style=\"color:#7a7a43;\">submit</span>();</pre> \n<p></p>', '0', '1', '0', '2019-05-24 00:02:03', '2019-05-24 00:02:03');
INSERT INTO `tb_blog` VALUES ('32', '6', '27', '几行js代码实现前端支持rails的data-remote=\"true\"', '<p> rails的<span class=\"webkit-html-attribute-name\" style=\"color:#222222;font-family:Menlo, monospace;font-size:11px;line-height:normal;\">data-remote</span><span style=\"color:#222222;font-family:Menlo, monospace;font-size:11px;line-height:normal;background-color:#FFFFFF;\">=\"</span><span class=\"webkit-html-attribute-value\" style=\"color:#222222;font-family:Menlo, monospace;font-size:11px;line-height:normal;\">true</span><span style=\"color:#222222;font-family:Menlo, monospace;font-size:11px;line-height:normal;background-color:#FFFFFF;\">\"很方便，链接加上这个属性点击链接即可在前端运行链接所指向的服务端返回的js代码。前端只需要下面几行代码</span> </p> \n<p> <span style=\"color:#222222;font-family:Menlo, monospace;font-size:11px;line-height:normal;background-color:#FFFFFF;\"><br> </span> </p> \n<p> <span style=\"color:#222222;font-family:Menlo, monospace;font-size:11px;line-height:normal;background-color:#FFFFFF;\"> </span> </p> \n<pre class=\" js;\">	$(document).ready(function () {\r\n		$(\"a[data-remote=true]\").click(function(e){\r\n			e.preventDefault();\r\n			$.getScript($(this).attr(\'href\'));\r\n		});\r\n	});</pre> \n<br> \n<p> <br> </p>', '0', '1', '0', '2019-05-24 00:02:03', '2019-05-24 00:02:03');
INSERT INTO `tb_blog` VALUES ('33', '6', '20', '用jquery实现post请求（非ajax）', '<p> <br> </p> \n<pre class=\" js;\">jQuery(function ($) {\r\n	$.extend({\r\n		form: function (url, data, method) {\r\n			if (method == null) method = \'POST\';\r\n			if (data == null) data = {};\r\n\r\n			var form = $(\'&lt;form&gt;\').attr({\r\n				method: method,\r\n				action: url\r\n			}).css({\r\n				display: \'none\'\r\n			});\r\n\r\n			var addData = function (name, data) {\r\n				if ($.isArray(data)) {\r\n					for (var i = 0; i &lt; data.length; i++) {\r\n						var value = data[i];\r\n						addData(name + \'[]\', value);\r\n					}\r\n				} else if (typeof data === \'object\') {\r\n					for (var key in data) {\r\n						if (data.hasOwnProperty(key)) {\r\n							addData(name + \'[\' + key + \']\', data[key]);\r\n						}\r\n					}\r\n				} else if (data != null) {\r\n					form.append($(\'&lt;input&gt;\').attr({\r\n						type: \'hidden\',\r\n						name: String(name),\r\n						value: String(data)\r\n					}));\r\n				}\r\n			};\r\n\r\n			for (var key in data) {\r\n				if (data.hasOwnProperty(key)) {\r\n					addData(key, data[key]);\r\n				}\r\n			}\r\n\r\n			return form.appendTo(\'body\');\r\n		}\r\n	});\r\n});</pre> \n<p> <br> </p> \n<p> 使用示例： </p> \n<p> <br> </p> \n<pre class=\" xml;\">$.form(\'/index\')\r\n\r\n&lt;form action=\"/index\" method=\"POST\"&gt;&lt;/form&gt;</pre> \n<pre class=\" xml;\">$.form(\'/new\', { title: \'Hello World\', body: \'Foo Bar\' })\r\n\r\n&lt;form action=\"/index\" method=\"POST\"&gt;\r\n    &lt;input type=\"hidden\" name=\"title\" value=\"Hello World\" /&gt;\r\n    &lt;input type=\"hidden\" name=\"body\" value=\"Foo Bar\" /&gt;\r\n&lt;/form&gt;</pre> \n<pre class=\" xml;\">$.form(\'/info\', { userIds: [1, 2, 3, 4] }, \'GET\')\r\n\r\n&lt;form action=\"/info\" method=\"GET\"&gt;\r\n    &lt;input type=\"hidden\" name=\"userIds[]\" value=\"1\" /&gt;\r\n    &lt;input type=\"hidden\" name=\"userIds[]\" value=\"2\" /&gt;\r\n    &lt;input type=\"hidden\" name=\"userIds[]\" value=\"3\" /&gt;\r\n    &lt;input type=\"hidden\" name=\"userIds[]\" value=\"4\" /&gt;\r\n&lt;/form&gt;</pre> \n<pre class=\" xml;\">$.form(\'/profile\', { sender: { first: \'John\', last: \'Smith\', postIds: null },\r\n                     receiver: { first: \'Foo\', last: \'Bar\', postIds: [1, 2] })\r\n\r\n&lt;form action=\"/profile\" method=\"POST\"&gt;\r\n    &lt;input type=\"hidden\" name=\"sender[first]\" value=\"John\"&gt;\r\n    &lt;input type=\"hidden\" name=\"sender[last]\" value=\"Smith\"&gt;\r\n    &lt;input type=\"hidden\" name=\"receiver[first]\" value=\"John\"&gt;\r\n    &lt;input type=\"hidden\" name=\"receiver[last]\" value=\"Smith\"&gt;\r\n    &lt;input type=\"hidden\" name=\"receiver[postIds][]\" value=\"1\"&gt;\r\n    &lt;input type=\"hidden\" name=\"receiver[postIds][]\" value=\"2\"&gt;\r\n&lt;/form&gt;</pre> \n<span style=\"color:#222222;font-family:\'Helvetica Neue\', Helvetica, Arial, sans-serif;font-size:15px;line-height:19.5px;background-color:#FFFFFF;\">With jQuery\'s&nbsp;</span>.submit()\n<span style=\"color:#222222;font-family:\'Helvetica Neue\', Helvetica, Arial, sans-serif;font-size:15px;line-height:19.5px;background-color:#FFFFFF;\">&nbsp;method you can create and submit a form with a simple expression:</span> \n<p> <br> </p> \n<p> <br> </p> \n<pre class=\" xml;\">$.form(\'http://stackoverflow.com/search\', { q: \'[ajax]\' }, \'GET\').submit();</pre> \n<p> <br> </p>', '0', '1', '1', '2019-05-24 00:02:04', '2019-05-24 00:02:04');
