INSERT INTO `tb_blog` VALUES ('55', '6', '19', '用vbscript脚本判断数组元素有没有重复', '&lt;SCRIPT LANGUAGE=\"vbScript\"&gt;\n<br>dim myarray,myarray1,myarray2,i\n<br>myarray=\"免费|活动|优惠|请进|活动|详情|电话\"\n<br>myarray1=split(myarray,\"|\")\n<br>do while i &lt; ubound(myarray1)\n<br>&nbsp;&nbsp;&nbsp; myarray2=split(myarray,myarray1(i))\n<br>&nbsp;&nbsp;&nbsp; if ubound(myarray2)&gt;1 then\n<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; document.write(myarray1(i)&amp;\"有重复&lt;BR&gt;\")\n<br>&nbsp;&nbsp;&nbsp; end if\n<br>&nbsp;&nbsp;&nbsp; i=i+1\n<br>loop\n<br>&lt;/SCRIPT&gt;', '0', '1', '0', '2019-05-24 00:06:18', '2019-05-24 00:06:18');
INSERT INTO `tb_blog` VALUES ('56', '6', '15', '利用正则表达式限制网页表单里的文本框输入内容', '<p>用正则表达式限制只能输入中文：onkeyup=\"value=value.replace(/[^\\u4E00-\\u9FA5]/g,\'\')\" onbeforepaste=\"clipboardData.setData(\'text\',clipboardData.getData(\'text\').replace(/[^\\u4E00-\\u9FA5]/g,\'\'))\" </p> \n<p>用正则表达式限制只能输入全角字符： onkeyup=\"value=value.replace(/[^\\uFF00-\\uFFFF]/g,\'\')\" onbeforepaste=\"clipboardData.setData(\'text\',clipboardData.getData(\'text\').replace(/[^\\uFF00-\\uFFFF]/g,\'\'))\" </p> \n<p>用正则表达式限制只能输入数字：onkeyup=\"value=value.replace(/[^\\d]/g,\'\') \"onbeforepaste=\"clipboardData.setData(\'text\',clipboardData.getData(\'text\').replace(/[^\\d]/g,\'\'))\" </p> \n<p>用正则表达式限制只能输入数字和英文：onkeyup=\"value=value.replace(/[\\W]/g,\'\') \"onbeforepaste=\"clipboardData.setData(\'text\',clipboardData.getData(\'text\').replace(/[^\\d]/g,\'\'))\" </p>', '0', '1', '0', '2019-05-24 00:06:18', '2019-05-24 00:06:18');
