INSERT INTO `tb_blog` VALUES ('58', '6', '19', '用asp判断字符串中是否含有汉字', '<div style=\"BORDER-RIGHT: #cccccc 1px solid; PADDING-RIGHT: 5px; BORDER-TOP: #cccccc 1px solid; PADDING-LEFT: 5px; BACKGROUND: #f3f3f3; PADDING-BOTTOM: 5px; MARGIN: 5px 20px; BORDER-LEFT: #cccccc 1px solid; PADDING-TOP: 5px; BORDER-BOTTOM: #cccccc 1px solid\"> \n <p>&lt;%<br>dim str<br>str=\"中国2008\"<br>if Len(Hex(asc(str)))&gt;2 then<br>&nbsp;&nbsp;&nbsp; response.write(\"字符串中含有汉字\")<br>else<br>&nbsp;&nbsp;&nbsp; response.write(\"字符串中没有汉字\")<br>end if<br>%&gt;</p>\n</div>', '0', '1', '1', '2019-05-24 00:06:19', '2019-05-24 00:06:19');
