INSERT INTO `tb_blog_evaluate` VALUES ('35', '87', '27', '获得祖先链：\n<br>function&nbsp;get_ancestors($arr,&nbsp;$e)\n<br>{\n<br>&nbsp;&nbsp;&nbsp;&nbsp;$ancestors&nbsp;=&nbsp;array();\n<br>&nbsp;&nbsp;&nbsp;&nbsp;foreach&nbsp;($arr&nbsp;as&nbsp;$a)&nbsp;{\n<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;($a-&gt;id&nbsp;==&nbsp;$e-&gt;parent_id)&nbsp;{\n<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$ancestors[]&nbsp;=&nbsp;$a;\n<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;($a-&gt;parent_id&nbsp;!=&nbsp;0)&nbsp;{\n<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$ancestors&nbsp;=&nbsp;array_merge(get_ancestors($arr,&nbsp;$a),&nbsp;$ancestors);\n<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n<br>&nbsp;&nbsp;&nbsp;&nbsp;}\n<br>&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$ancestors;\n<br>}', '0', '0', '2019-05-24 00:16:00', '2019-05-24 00:16:00');
