INSERT INTO `tb_post` VALUES ('49', '6', '26', null, 'jquery DOMEval函数的理解', '<div style=\"font-weight:bold; margin-bottom:8px;\">\n jquery DOMEval函数的理解\n</div> \n<div style=\"font:bold 12px 宋体;\">\n <img style=\"margin-bottom:-2px;\" src=\"zzz/editor/img/code.gif\">程序代码：\n</div>\n<pre class=\"code\">function DOMEval( code, doc, node ) {\n        doc = doc || document;\n\n        var i,\n            script = doc.createElement( \"script\" );\n\n        script.text = code;\n        if ( node ) {\n            for ( i in preservedScriptAttributes ) {\n                if ( node[ i ] ) {\n                    script[ i ] = node[ i ];\n                }\n            }\n        }\n        doc.head.appendChild( script ).parentNode.removeChild( script );//特别是这一句代码怎么理解\n    }</pre> \n<div id=\"post_rate_div_2665228\"></div>', '0', '1', '25', '2019-05-15 20:17:08', '2019-05-15 20:17:08');
