INSERT INTO `tb_post` VALUES ('56', '6', '27', null, '自己写的轮播图，用隐藏写的，但左右两个按钮不知道怎么和分页器图片同步', '<div style=\"font-weight:bold; margin-bottom:8px;\">\n 自己写的轮播图，用隐藏写的，但左右两个按钮不知道怎么和分页器图片同步\n</div> 小白一个，左右两个按钮不知道怎么同步，网上教程的都是用推动。加超过范围隐藏。但我还是想先搞懂用隐藏消失做\n<br> \n<br> &lt;script type=\"text/javascript\" src=\"js/jquery.js\"&gt;&lt;/script&gt;\n<br> &nbsp;&nbsp;&nbsp; &lt;style type=\"text/css\"&gt;\n<br> &nbsp;&nbsp;&nbsp; ul {\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;list-style: none;\n<br> &nbsp;&nbsp;&nbsp; }\n<br> \n<br> &nbsp;&nbsp;&nbsp; .box {\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;width: 346px;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;height: 300px;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;overflow: hidden;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;position: relative;\n<br> &nbsp;&nbsp;&nbsp; }\n<br> \n<br> &nbsp;&nbsp;&nbsp; .box_img {\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;width: 346px;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;height: 250px;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;overflow: hidden;\n<br> &nbsp;&nbsp;&nbsp; }\n<br> \n<br> &nbsp;&nbsp;&nbsp; .box_img ul li {\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;display: none;\n<br> &nbsp;&nbsp;&nbsp; }\n<br> \n<br> &nbsp;&nbsp;&nbsp; .box_img a {\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;display: block;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;width: 346px;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;height: 250px;\n<br> &nbsp;&nbsp;&nbsp; }\n<br> \n<br> &nbsp;&nbsp;&nbsp; .box_xb {\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;position: absolute;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;bottom: 0;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;text-align: center;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;width: 346px\n<br> &nbsp;&nbsp;&nbsp; }\n<br> \n<br> &nbsp;&nbsp;&nbsp; .box_xb a {\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;display: inline-block;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;padding: 2px 10px;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;font-size: 10px;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;background: #fff;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;margin: 0 5px;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;color: #000;\n<br> &nbsp;&nbsp;&nbsp; }\n<br> \n<br> &nbsp;&nbsp;&nbsp; .box_xb a.active {\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;background: red;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;color: #000;\n<br> &nbsp;&nbsp;&nbsp; }\n<br> \n<br> &nbsp;&nbsp;&nbsp; .box-anniu a {\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;display: block;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;width: 20px;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;height: 20px;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;background: red;\n<br> &nbsp;&nbsp;&nbsp; }\n<br> \n<br> &nbsp;&nbsp;&nbsp; #left {\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;position: absolute;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;left: 0;\n<br> &nbsp;&nbsp;&nbsp; }\n<br> \n<br> &nbsp;&nbsp;&nbsp; #right {\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;position: absolute;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;right: 0;\n<br> &nbsp;&nbsp;&nbsp; }\n<br> &nbsp;&nbsp;&nbsp; &lt;/style&gt;\n<br> &lt;/head&gt;\n<br> \n<br> &lt;body&gt;\n<br> &nbsp;&nbsp;&nbsp; &lt;div class=\"box\"&gt;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;div class=\"box_img\"&gt;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;ul&gt;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;li&gt;&lt;a href=\"#\" style=\"background: #f90;\"&gt;&lt;/a&gt;&lt;/li&gt;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;li&gt;&lt;a href=