INSERT INTO `tb_post` VALUES ('104', '6', '26', null, '高手们指教！浏览器提示错误！但是找不出原因！谢谢', '<div style=\"font-weight:bold; margin-bottom:8px;\">\n 高手们指教！浏览器提示错误！但是找不出原因！谢谢\n</div> &lt;html&gt;\n<br> &lt;head&gt;\n<br> &lt;title&gt;&lt;/title&gt;\n<br> &lt;script language=\"javascript\" type=\"text/javascript\"&gt;\n<br> var xmlHttp;\n<br> function createXMLHttpRequest()\n<br> {\n<br> &nbsp;&nbsp;&nbsp;if(window.ActiveXObject)\n<br> &nbsp;&nbsp;&nbsp; {\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; xmlHttp=new ActiveXObject(\"Microsoft.XMLHTTP\");\n<br> &nbsp;&nbsp;&nbsp; }\n<br> &nbsp;&nbsp;&nbsp;else\n<br> &nbsp;&nbsp;&nbsp; {\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;xmlHttp=new XMLHttpRequest();&nbsp;&nbsp;\n<br> &nbsp;&nbsp;&nbsp; }\n<br> }\n<br> function startRequest()\n<br> {\n<br> &nbsp;&nbsp;&nbsp;createXMLHttpRequest();\n<br> &nbsp;&nbsp;&nbsp;xmlHttp.OnReadyStateChange=handlestateChange();\n<br> &nbsp;&nbsp;&nbsp;xmlHttp.open(\"GET\",\"src.xml\",true);\n<br> &nbsp;&nbsp;&nbsp;xmlHttp.send(null);\n<br> }\n<br> function handlestateChange()\n<br> {\n<br> &nbsp;&nbsp;if(xmlHttp.readyState==4)\n<br> &nbsp;&nbsp;&nbsp;{\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(xmlHttp.status==200)\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert(\"the server replied with:\"+xmlHttp.responseText);\n<br> \n<br> &nbsp;&nbsp;&nbsp;}\n<br> }\n<br> &lt;/script&gt;\n<br> &lt;/head&gt;\n<br> &lt;body&gt;\n<br> &lt;form action=\"#\"&gt;\n<br> &nbsp;&nbsp;&lt;input type=\"button\" value=\"start Basic\" onclick=\"startRequest();\"/&gt;\n<br> &lt;/form&gt;\n<br> &lt;/body&gt;\n<br> &lt;/html&gt; \n<div style=\"margin:25px 0px 0px 0px; font-size:12px;\">\n 搜索更多相关主题的帖子: \n <a href=\"tag-%CC%E1%CA%BE.html\" target=\"_blank\"><font color=\"red\"><b>提示</b></font></a>&nbsp;&nbsp;\n <a href=\"tag-%D6%B8%BD%CC.html\" target=\"_blank\"><font color=\"red\"><b>指教</b></font></a>&nbsp;&nbsp;\n <a href=\"tag-%E4%AF%C0%C0%C6%F7.html\" target=\"_blank\"><font color=\"red\"><b>浏览器</b></font></a>&nbsp;&nbsp; \n</div> \n<div id=\"post_rate_div_1667727\"></div>', '0', '1', '1', '2019-05-15 20:28:39', '2019-05-15 20:28:39');
