INSERT INTO `tb_post` VALUES ('118', '4', '19', null, '创建servlet失败，用耿祥义jsp实用教程书中的例子', '<div style=\"font-weight:bold; margin-bottom:8px;\">\n 创建servlet失败，用耿祥义jsp实用教程书中的例子\n</div> 我弄了好多遍，不像是路径的原因&nbsp;&nbsp;它的书上编译写的是javac Hello.java 可是类名明明是 Example5_1_Servlet 为什么servlet-name是hello我也不懂 可是书上又说如果没有名为hello的servlet 服务器会创建一个 我是一个初学者，下面是代码\n<br> \n<br> package myservlet.control;\n<br> import java.io.*;\n<br> import javax.servlet.*;\n<br> import javax.servlet.http.*;\n<br> public class Example5_1_Servlet extends HttpServlet\n<br> {\n<br> &nbsp;&nbsp;&nbsp; public void init(ServletConfig config) throws ServletException{\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;super.init(config);\n<br> &nbsp;&nbsp;&nbsp; }\n<br> &nbsp;&nbsp;&nbsp; public void service(HttpServletRequest reqest,HttpServletResponse response) throws IOException{\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;reqest.setCharacterEncoding(\"gb2312\");\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String str=reqest.getParameter(\"moon\");\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;response.setContentType(\"text/html;charset=gb2312\");\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;PrintWriter out=response.getWriter();\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String sevletName=getServletName();\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;out.println(\"&lt;html&gt;&lt;body bgcolor=#EEDDFF&gt;\");\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;out.println(\"&lt;b&gt;请求的servlet的名字是\"+sevletName+\"&lt;br&gt;&lt;/b&gt;\");\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;out.println(\"&lt;b&gt;servlet在Web设计中非常重要（见稍后的第6章）&lt;/b&gt;\");\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(str!=null&amp;&amp;str.length()&gt;=1)\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;out.println(\"&lt;br&gt;&lt;h2&gt;\"+str+\"&lt;h2&gt;\");\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;out.println(\"&lt;/body&gt;&lt;/html&gt;\");\n<br> &nbsp;&nbsp;&nbsp; }\n<br> }\n<br> \n<br> &lt;?xml version=\"1.0\" encoding=\"iso-8859-1\"?&gt;\n<br> &lt;web-app&gt;\n<br> &nbsp;&nbsp;&nbsp; &lt;servlet&gt;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;servlet-name&gt;hello&lt;/servlet-name&gt;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;servlet-class&gt;myservlet.control.Example5_1_Servlet&lt;/servlet-class&gt;\n<br> &nbsp;&nbsp;&nbsp; &lt;servlet&gt;\n<br> &nbsp;&nbsp;&nbsp; &lt;servlet-mapping&gt;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;servlet-name&gt;hello&lt;/servlet-name&gt;\n<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;url-pattern&gt;/lookHello&lt;/url-pattern&gt;\n<br> &nbsp;&nbsp;&nbsp; &lt;servlet-mapping&gt;\n<br> &lt;/web-app&gt;\n<br> \n<br> \n<div style=\"margin:25px 0px 0px 0px; font-size:12px;\">\n 搜索更多相关主题的帖子: \n <a href=\"tag-package.html\" target=\"_blank\"><font color=\"red\"><b>package</b></font></a>&nbsp;&nbsp;\n <a href=\"tag-public.html\" target=\"_blank\"><font color=\"red\"><b>public</b></font></a>&nbsp;&nbsp;\n <a href=\"tag-%B7%FE%CE%F1%C6%F7.html\" target=\"_blank\"><font color=\"red\"><b>服务器</b></font></a>&nbsp;&nbsp;\n <a href=\"tag-import.html\" target=\"_blank\"><font color=\"red\"><b>import</b></font></a>&nbsp;&nbsp; \n</div> \n<div id=\"post_rate_div_2569368\"></div>', '0', '1', '0', '2019-05-21 10:00:23', '2019-05-21 10:00:23');
