INSERT INTO `tb_post` VALUES ('134', '4', '14', null, 'jsp使用jfreechart折线图，为什么页面显示不了图片？？求解求解', '<div style=\"font-weight:bold; margin-bottom:8px;\">\n jsp使用jfreechart折线图，为什么页面显示不了图片？？求解求解\n</div> jsp使用jfreechart折线图，为什么图片在tomcat底下的temp文件夹下可以生成，却不能再网页上显示，按网上的配置web.xml文件的内容，还是不行。 \n<div style=\"margin:25px 0px 0px 0px; font-size:12px;\">\n 搜索更多相关主题的帖子: \n <a href=\"tag-%CE%C4%BC%FE%BC%D0.html\" target=\"_blank\"><font color=\"red\"><b>文件夹</b></font></a>&nbsp;&nbsp;\n <a href=\"tag-%CD%BC%C6%AC.html\" target=\"_blank\"><font color=\"red\"><b>图片</b></font></a>&nbsp;&nbsp;\n <a href=\"tag-%CD%F8%C9%CF.html\" target=\"_blank\"><font color=\"red\"><b>网上</b></font></a>&nbsp;&nbsp;\n <a href=\"tag-%CD%F8%D2%B3.html\" target=\"_blank\"><font color=\"red\"><b>网页</b></font></a>&nbsp;&nbsp; \n</div> \n<div id=\"post_rate_div_2490848\"></div>', '-15', '1', '0', '2019-05-21 10:01:11', '2019-05-21 10:01:11');
INSERT INTO `tb_post` VALUES ('135', '4', '17', null, '新手，求大神给代码实现多边形内的白色半透明层给去掉。', '<div style=\"font-weight:bold; margin-bottom:8px;\">\n 新手，求大神给代码实现多边形内的白色半透明层给去掉。\n</div> //创建经纬度数组&nbsp;&nbsp;冼村街道\n<br> var xiancunCenter = new BMap.Point(113.340128,23.128283);\n<br> var xiancun = [new BMap.Point(113.327839,23.133434),new BMap.Point(113.327696,23.118844),new BMap.Point(113.349722,23.116584),new BMap.Point \n<br> (113.350153,23.131939)];\n<br> //创建多边形\n<br> var xiancunPolygon = new BMap.Polygon(xiancun, {strokeColor:\"blue\", strokeWeight:5, strokeOpacity:0.5});\n<br> //添加多边形到地图上&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n<br> map.addOverlay(xiancunPolygon);\n<br> xiancunPolygon.addEventListener(\"mouseover\",function(){&nbsp;&nbsp;&nbsp; xiancunPolygon.setStrokeColor(\"red\");});\n<br> xiancunPolygon.addEventListener(\"mouseout\",function(){&nbsp;&nbsp;&nbsp; xiancunPolygon.setStrokeColor(\"blue\");});\n<br> xiancunPolygon.addEventListener(\"click\",function(){&nbsp;&nbsp;&nbsp; map.zoomIn();&nbsp;&nbsp;&nbsp; xiancunPolygon.setStrokeColor(\"red\");&nbsp;&nbsp;&nbsp; map.setCenter(xiancunCenter);});\n<br> var xiancunLabel2 = new BMap.Label(\"冼村\",{offset: new BMap.Size(10,-20), position: xiancunCenter});xiancunLabel2.setStyle({\"padding\":&nbsp;&nbsp;\n<br> \"2px\"});map.addOverlay(xiancunLabel2);\n<br> \n<br> \n<div style=\"margin:25px 0px 0px 0px; font-size:12px;\">\n 搜索更多相关主题的帖子: \n <a href=\"tag-function.html\" target=\"_blank\"><font color=\"red\"><b>function</b></font></a>&nbsp;&nbsp;\n <a href=\"tag-%BE%AD%CE%B3%B6%C8.html\" target=\"_blank\"><font color=\"red\"><b>经纬度</b></font></a>&nbsp;&nbsp; \n</div> \n<div id=\"post_rate_div_2441885\"></div>', '0', '1', '0', '2019-05-21 10:01:12', '2019-05-21 10:01:12');
